import jwt from 'jsonwebtoken';


export const isAuthenticated = () => {

  const token = localStorage.getItem('token'); // Retrieve the token from local storage or wherever you store it

  if (token) {
    // If a token exists, verify its validity
    console.log("USER IS LOGGED IN: "  + token)

    try {

     // const decodedToken = jwt.verify(token, 'test'); // Replace 'your-secret-key' with your actual secret key

      return true; // Token is valid and user is authenticated
    } catch (error) {
      console.error('Invalid token:', error);
    }
  }

  return false; // Token doesn't exist or is invalid, user is not authenticated
};
